import React, { Component } from 'react'
import {Link, Redirect} from 'react-router-dom'
import './Area.css'
import ReactTable from 'react-table';
import 'react-table/react-table.css'

import axios from 'axios'
import authService from '../../services/auth-service';
import axiosService from '../../services/axios-service';
import { API_URL } from '../../services/url';

//
export class ViewArea extends Component {
    constructor () {
        super() 
        this.state = {
          userdata : [],
          detailsdata : []
        }
        
    }
    handleCheck=(item) =>{
      console.log(item.area_id);
      let sitemeet = item.area_id;
      axios.delete(`${API_URL}area/delete/${sitemeet}`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then(response => {
                
                console.log(response)
                if(response.data.success === true)
                {

                  // console.log('Area delete view');
                  alert(response.data.msg)
                 window.location.reload();
                  
                }
                else{
                                 console.log(response.data.msg);
                             }
          
              }) .catch(error => {
                alert(error.response.data.msg)
                //console.log(error);
              })
     }
    componentWillMount ()
    {
      this.handleClick();
    }
    handleClick () {
      axios.get(`${API_URL}area/view`,axios.defaults.headers.common['authorization'] = 'Bearer ' +	authService.getToken())
      .then(response => {
                console.log('Area view')
                console.log(response.data.area_data);
                const data = response.data.area_data;
                this.setState({ userdata : data })
              }) .catch(error => {
                
                console.log(error);
              })
      }
    render() {
      let serial = 0;
      
      const col=[
        {
          Header:'Sr.No',
          id:"row",
          Cell: (row) => {
            return <div>{row.index+1}</div>;
          }
          
          
        },
              {
          Header:'Area_Name',
          accessor:"area_name"
        },
        {
          Header:'Area_Brief',
          accessor:"null"
        },
        {
          Header:'Action',
  
          Cell :(props) =>{
        return <div>{
                  this.state.userdata.map(function(item) {
          <button  className="btn btn-danger custom-edit-btn btn-sm"
          onClick={(item) => this.handleCheck(item)}>
            <i class="fa fa-trash-o" aria-hidden="true"></i>
              </button>
        })
      }
                  </div>
          
        }
      }
      ]
